import React, { Component } from 'react';
import './App.css';

import RateSort from './RateSort.js';
import MoneySort from './MoneySort.js'
import List from './List.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],//json容器
      isLoaded: false,
      rateSort: "highRate",//預設為評分高到低
      priceSort: 0
    }
  }

  componentDidMount() {
    // 會等render跑完再進行

    fetch("https://interview.tripresso.com/tour/search")
      .then(res => res.json()) //轉成JSON格式
      .then(res => {
        this.setState({
          isLoaded: true,
          items: res
        })
      });
  }
  handleSort = (e) => {
    console.log(e.target.value);
    this.setState({ sort: e.target.value });//存入排序方式
  }

  render() {

    let { isLoaded, items } = this.state;

    if (!isLoaded) {//確認api是否連接成功
      return <div>Loading...</div>;
    }
    else {
      //對評分進行排序
      const sortedData = items.data.tour_list.sort((a, b) => {
        if (this.state.sort === "highRate") {
          return (b.rating) - (a.rating)
        }
        else if (this.state.sort === "lowRate") {
          return (a.rating) - (b.rating)
        }
      })
      return (
        <div className="App">
          <RateSort handleSort={this.handleSort}></RateSort>
          <MoneySort></MoneySort>
          <List data={sortedData}></List>
        </div>

      );
    }

  }
}
export default App;
