import React, { Component } from 'react';
import './App.css';
import styled from 'styled-components';
import SortButton from './SortButton.js'
import Items from './Items.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],//json容器
      isLoaded: false,
      sort: "highRate",//預設為評分高到低
    }
  }

  componentDidMount() {
    // 會等render跑完再進行

    fetch("https://interview.tripresso.com/tour/search")
      .then(res => res.json()) //轉成JSON格式
      .then(res => {
        this.setState({
          isLoaded: true,
          items: res
        })
      });
  }
  handleSort = (e) => {
    console.log(e.target.value);
    this.setState({ sort: e.target.value });
  }

  render() {

    let { isLoaded, items } = this.state;

    if (!isLoaded) {//確認api是否連接成功
      return <div>Loading...</div>;
    }
    else {
      //對評分進行排序
      const sortedData = items.data.tour_list.sort((a,b)=>{
        if(this.state.sort==="highRate"){
          return  (b.rating) - (a.rating)
        }
        else if (this.state.sort==="lowRate"){
          return  (a.rating) - (b.rating)

        }
      })
      return (
        <div className="App">
          <SortButton handleSort={this.handleSort}></SortButton>
          { items.data.tour_list.map(data => (
            <li key={data.id}>
              {data.title}
              <Box src={data.image_url} alt=""></Box>
              <p>推薦指數{data.rating}</p>
            </li>
          ))}
        </div>

      );
    }

  }
}
export default App;

const Box = styled.img`
height:200px;
width:200px;
`
