import React, { Component } from 'react';
import './App.css';
import styled from 'styled-components';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      sort:"",
    }
  }

  componentDidMount() {
    // 會等render跑完再進行
    fetch("http://interview.tripresso.com/tour/search?page=2&row_per_page=5&sort=price_asc")
      .then(res => res.json()) //轉成JSON格式
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  render() {

    let { isLoaded, items } = this.state;
    if (!isLoaded) {//確認api是否連接成功
      return <div>Loading...</div>;
    }
    else {
      return (
        <div className="App">

            { items.data.tour_list.map(data=>(
            <li key={data.id}>
              {data.title}
              <Box src={data.image_url} alt=""></Box>
            <p>推薦指數{data.rating}</p>
            </li>
          ))} 
         </div>

      );
    }

  }
}
export default App;

const Box = styled.img`
height:200px;
width:200px;
`
