import React, { Component } from 'react';
import './App.css';




class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    // 會等render跑完再進行
    fetch("https://interview.tripresso.com/tour/search")
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
  }

  render() {

    let { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    }
    else {
      return (
        <div className="App">
          {items.data.tour_list.map(data=>(
            <li key={data.id}>
              {data.title}
              <img src="data.image_url" alt=""/>
            </li>
          ))}
          img
        </div>
      );
    }

  }
}
export default App;
